value.css:
section {
    padding: 7.5rem 0;
    text-align: center;
  }
.container {
    max-width: 66%;
    padding: 120px 0;
  }
h4 {
    color: #353535;
    font-size: 40px;
    line-height: 50px;
    font-family: 'Spectral', serif;
  }
h4 a {
      text-decoration: underline;
    }

section {
  height: 85vh;
  position: relative;
  padding-top: 235px;
}

.superhead {
  color: #757575;
  font-size: 10px;
  letter-spacing: 5px;
  text-transform: uppercase;
  margin-bottom: 0.25rem;
}

.headline {
  color: #19191f;
  font-size: 30px;
  line-height: 40px;
  font-family: 'Spectral', serif;
  margin-bottom: 1.5rem;
  max-width: 500px;
}

.button {
  position: relative;
  display: inline-block;
  font-weight: medium;
  padding: 0 25px;
  background: #5050FF;
  border-radius: 1px;
  transition: transform 0.3s;
  transform-origin: 50% 0;
  transform-style: preserve-3d;
  line-height: 40px;
  -webkit-perspective: 1000px;
  -moz-perspective: 1000px;
  perspective: 1000px;
  color: #ffffff;
}

.container {
  padding-top: 45px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.logo {
  color: #454545;
  font-size: 25px;
  font-weight: normal;
  line-height: 40px;
}

ul {
  display: flex;

  li {
    margin-left: 2rem;

    a {
      font-size: 16px;
      color: #757575;
      line-height: 40px;
      padding-right: 22px;
      padding-left: 22px;
      transition: all ease-in-out 0.4s;
    }
  }
}
