value.css:
section {
  display: block;
  width: 80%;
  margin: auto;
  background-color: rgba(255, 255, 255);
  padding: 3rem 0 5rem 0;

  h2 {
    color: #353535;
    font-size: 40px;
    line-height: 50px;
    font-family: 'Spectral', serif;
    margin: 1rem 0 1rem 0;
    font-weight: normal;
  }
  .content {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 2.5rem;

    img {
      display: block;
      max-width: 100%;
      height: auto;
      overflow: hidden;
      transition: all ease-in-out 0.4s;    
    }
    img:hover {
        background: #ffffff;
        box-shadow: 0px 16px 22px 0px rgba(90, 91, 95, 0.3);
        top: -5px;      	
    }
     .lil {
        color: #858585;
        font-size: 10px;
        letter-spacing: 2px;
        text-transform: uppercase;
        font-weight: normal;
        margin-bottom: 0.5rem;
        padding-top: 12px;
        padding-left: 10px;
      }
      span {
        color: #454545;
        font-weight: normal;
        font-size: 20px;
        font-family: 'Spectral', serif;
        line-height: 1.1;
        padding-left: 10px;
        }
	}
}
