section {
  display: flex;
  justify-content: center;
  padding: 30px 15px 0px 15px;
  margin-right: auto;
  margin-left: auto;
  div {
      display: grid;
    	grid-auto-flow: row;
   	h3 {
    color: #353535;
    font-size: 40px;
    line-height: 50px;
    font-family: 'Spectral', serif;
    font-weight: normal;
    margin: .62em 0;
    width: 700px;
    text-align: center;
  a {
    color: #000000;
    text-decoration: underline;
  }
  a:hover {
    color: #5050FF;
    transition: 0.5s;
    text-decoration: none;
  	} 
	} 
  footer {
    display: grid;
    grid-auto-flow: column;
    gap: 4rem;

    .first
    {
      display: grid;
    	grid-auto-flow: column;

    p {
      color: #959595;
      font-size: 14px;
      font-weight: 300;
      line-height: 30px;
      letter-spacing: 0.5px;
      padding-top: 6px;
      margin: 0 0 10px;
      a {
         color: #5050FF;
      }
      span:last-child {
        margin: 0;
        padding: 0;
      }
    }
    }
    .second {
      display: flex;
    	a {
      background: #191919;
      border-radius: 100%;
      color: #ffffff;
      cursor: pointer;
      transition: all ease-in-out 0.4s;
      width: 35px;
      height: 35px;
      line-height: 35px;
      text-align: center;
      margin: 4px 4px 0 4px;

      }
      a:hover {
        background-color: #5050FF;
        transition: 0.5s;
      }
    }
    }
  }
}

